import React ,{useContext, useEffect,useState} from 'react'
import { myContext } from '../../contex';
import {Button} from 'react-bootstrap'
import axios from '../../Axios'
import MealCard from '../../components/MealCard';
import { LinkContainer } from 'react-router-bootstrap';
function Favorites() {
const {user} = useContext(myContext);
const [favorites, setFavorites] = useState([]);
useEffect(() => {
  //www.themealdb.com/api/json/v1/1/lookup.php?i=52772
  if(user.favorites.length){
   const requests = user.favorites.map((favorite)=> //going by all user favorites (favorite its a array with meal id`s which user selected to be a favorite) and to to each of them fetch to REST API by its own favorite id
   
    fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${favorite}`)
    .then((res)=> res.json())
   );
   Promise.all(requests).then((res)=>setFavorites(res)) //at the end all the results from REST API we going and setFavorites to results
  }
}, []);
if(!user.favorites.length){
	return(
	<div>
	  <h3> You dont have any favorites yet</h3>
	  <LinkContainer to="/">
	  <Button>Please add new ones</Button>
	  </LinkContainer>
	</div>
	)
}

  return (

    <div> <h3> Your Favorites</h3>
  <div className="meals-container">
    {favorites.map(({meals:meal})=>(
          <MealCard key={meal[0].idMeal} {...meal[0]}/>

))};
   
    </div>
    </div>
  )
}

export default Favorites;
